      const querySnapshot = await getDocs(q);
      const gotStocks = [];
      querySnapshot.forEach((doc) => {
        const dateData = doc.data();
        let stocks = JSON.parse(dateData['Stocks']);
        stocks.forEach(data => {
          gotStocks.push({
            name: data.name,
            quantity: data.quantity,
            cost: data.cost,
          });
        });
