exports.default = _default;
function reorderList(selectedList) {
  var selectedItems = [];
  var nonSelectedItems = [];
  selectedList.items.forEach(function (item) {
    if (item.selected) {
      selectedItems.push(item);
    } else {
      nonSelectedItems.push(item);
    }
  });
  var sortedItems = nonSelectedItems.concat(selectedItems);
  selectedList = Object.assign({}, selectedList, {
    items: sortedItems
  });
  return selectedList;
}

