function reorderList(selectedList: any) {
  const selectedItems = [];
  const nonSelectedItems = [];

  selectedList.items.forEach((item) => {
    if (item.selected) {
      selectedItems.push(item);
    } else {
      nonSelectedItems.push(item);
    }
  });

  const sortedItems = nonSelectedItems.concat(selectedItems);
  selectedList = {
    ...selectedList,
    items: sortedItems,
  };
  return selectedList;
}
